INSERT INTO `check_group_rule` VALUES (24, 2, 'Ⅳ-38.主次干道、商业大街', '\n①道路整体清洁，路面呈本色，发现一处道路不清洁，路面有污迹、油污、灰尘，未呈本色记1笔；\n②发现人行道、路缘石、交通护栏、隔离墩、绿化隔离带一处未保持干净、有垃圾，发现一处记1笔。', 19, 1, 0.25, '道路整体不清洁，路面未呈本色，有污迹、油污、灰尘等', 2);
