INSERT INTO `evaluation_rules` VALUES (121, 42, '3）建成区造成人员伤亡交通事故起数、死亡人数连续3年同比下降，未发生一次死亡3人以上较大道路交通事故。', 'jcqzcrys', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (122, 43, '1）开展网上精神文明创建，做好网上正面宣传，强化网络文明自律，规范网络传播秩序，加强网络诚信建设，开展网络公益活动，倡导文明办网、文明上网；', 'kzwsjswm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (123, 43, '2）建好用好地方文明网及相关新媒体平台，壮大精神文明建设网上宣传声势。', 'jhyhdfwm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (124, 44, '1）建立健全道德模范、身边好人等先进模范推选、帮扶、礼遇、管理制度，发挥先进模范示范表率作用；', 'jljqddmf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (125, 44, '2）运用各类媒体广泛宣传道德模范、身边好人等先进模范事迹精神，综合运用宣讲报告、广播电视节目、公益广告、专题展览、文学作品等形式广泛开展先进模范学习宣传活动。', 'yyglmtgf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (126, 45, '1）传统媒体、网络媒体、新媒体平台持续刊播公益广告；', 'ctmtwlm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (127, 45, '2）坚持思想性、艺术性、观赏性、耐久性相结合，设计制作与城市景观相融合、与城市历史文化相承接、与市民接受方式和欣赏习惯相契合的公益广告，在社会公共场所、公共交通工具、建筑工地围挡等显著位置刊播展示，把社会主义核心价值观和文明风尚有机融入各类生活场景。', 'jcsxxys', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (128, 46, '1）下辖县（市、区）建设新时代文明实践中心，乡镇（街道）建设新时代文明实践所，行政村（社区）建设新时代文明实践站，在新时代文明实践中心成立县级志愿服务促进中心，组织开展形式多样的文明实践活动，落实好学习实践科学理论、宣传宣讲党的政策、培育践行主流价值、丰富活跃文化生活、持续深入移风易俗等工作任务；', 'xxxsq', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (129, 46, '2）县、乡镇（街道）、村（社区）三级党组织落实主体责任，有效整合基层公共服务资源，提供政策支持和经费保障，建立健全文明实践运行机制。', 'xxzjd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (130, 47, '1）加强文明实践志愿服务队伍建设，县级志愿服务总队配置理论政策宣讲、文化文艺服务等志愿服务队伍，完善文明实践志愿服务注册登记、培训管理、褒奖激励、物质保障、技术支持等规章制度，为注册志愿者提供必要保险，维护志愿者合法权益；', 'jqwmsjzy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (131, 47, '2）打造精准对接群众需求的文明实践志愿服务项目，形成“点单”“派单”“接单”“评单”相贯通的工作模式，群众参与度和满意度持续提升；', 'dzjzdjqz', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (132, 47, '3）建立平战结合的应急志愿服务体系，完善联动配合、信息共享、培训演练等工作机制，培育医疗、救援、科技、心理疏导等专业志愿队伍。', 'jlpzjhdy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (133, 48, '1）落实中央文明委《关于推进志愿服务制度化的意见》，健全志愿服务体系，建立志愿服务工作协调机构，形成党委宣传部、文明办牵头志愿服务的工作机制；', 'lszywmw', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (134, 48, '2）大力弘扬志愿精神，积极培育志愿文化，开展志愿服务展示交流活动，宣传推选优秀志愿者以及志愿服务项目、组织、社区等志愿服务先进典型，市民对志愿服务活动认同和支持率≥90%，有志愿服务时间记录的志愿者人数占注册志愿者总人数的比例≥50%；', 'dlhyzyjs', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (135, 48, '3）以社区、公共文化设施、景区景点、医院、交通场站、政务大厅和新时代文明实践中心（所、站）为重点，建立有人员、有项目、有管理的志愿服务站点。', 'ysqggwh', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (136, 49, '1）开展志愿服务关爱行动，以空巢老人、留守儿童、困境儿童、残疾人等困难群体为重点做好结对帮扶；', 'kzzyfwga', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (137, 49, '2）组织开展文明旅游、文明交通、文化文艺、助学支教、智慧助老、医疗健康、环境保护、法律援助、科学普及、社会治安等志愿服务活动，围绕国家及区域重大发展战略、重要赛会等开展相关志愿服务活动，在全市范围内开展时间3年以上的志愿服务品牌项目数量≥3个。', 'zzkzwmly', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (138, 50, '落实中央文明委《关于深化群众性精神文明创建活动的指导意见》要求，立足新发展阶段、贯彻新发展理念、构建新发展格局，结合本地实际制定实施文明创建工程的工作规划或工作方案，以文明城市创建为龙头高质量推进文明创建工作。', 'lszywmwgy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (139, 51, '1）落实中共中央、国务院《关于全面推进乡村振兴加快农业农村现代化的意见》要求，实施乡村建设行动，加强乡村公共基础设施建设，完善农村公路交通基础设施和管理设施，实施数字乡村建设发展工程，提升村级综合服务水平；', 'lszgzyg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (140, 51, '2）实施农村人居环境整治提升五年行动，建设美丽乡村，推进厕所革命，治理农村污水、黑臭水体及面源污染，健全农村生活垃圾收运处置体系，市辖区内90%以上行政村垃圾得到治理，农村自来水普及率≥88%，农村环境干净整洁、管理规范有序、无明显脏乱差现象、无露天焚烧秸秆落叶现象；', 'ssncrjhj', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (141, 51, '3）加强农村思想道德建设，运用积分制、道德评议会、红白理事会、村规民约等方式开展移风易俗、破除陈规陋习，推进文化、科技、卫生“三下乡”经常化，建好用好乡村复兴少年宫，深化文明户、文明村、文明镇（乡）创建，县级以上文明村镇占比≥60%；', 'jqncsxdd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (142, 52, '1）落实中央文明委《关于深化新时代文明单位创建工作的意见》要求，在机关、企事业单位、街道社区广泛开展文明单位创建活动；', 'lszywmw', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (143, 52, '2）推动文明单位创建活动向非公有制经济组织和社会组织延伸，新推选的市级文明单位中非公组织占比≥10%；', 'tdwmdwcj', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (144, 52, '3）文明单位积极参与所在地方的文明城市创建，开展志愿服务活动，主动承担社会责任。', 'wmdwjjcy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (145, 53, '1）加强家庭、家教、家风建设，开展传承好家风好家训活动，在重要传统节日和时间节点组织家庭文明建设活动，加强青年婚恋观、家庭观教育；', 'jqjtjj', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (146, 53, '2）建立健全文明家庭推选、关爱、管理机制，各类媒体广泛宣传文明家庭先进事迹。', 'jljqwmjt', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (147, 54, '1）推进党的创新理论进课堂，强化师德师风建设，开展形式多样的校园文化活动，加强学生思想品德教育、健康教育、劳动教育、节能环保教育、安全教育，落实未成年人保护法，加强校园欺凌行为预防治理，培养德智体美劳全面发展的社会主义建设者和接班人；', 'tjddcxll', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (148, 55, '1）落实《中共中央关于加强党的政治建设的意见》，完善全面从严治党制度，强化党委主体责任和纪委监督责任，加强党的领导和监督；', 'lszgzyg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (149, 55, '2）全面加强党的纪律建设，用好“四种形态”，综合发挥惩治震慑、惩戒挽救、教育警醒的功效。', 'qmjqddjl', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (150, 56, '1）深化“双随机、一公开”监管，推进政府综合执法，完善行政执法管理；', 'shssjy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (151, 56, '2）深化简政放权、放管结合、优化服务，改革行政审批制度，压缩审批时限，减少审批事项，优化审批程序，提高政府效能。', 'shjzfqf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (152, 57, '全面实行政府权责清单制度，健全重大政策事前评估和事后评价制度，推进政务服务标准化、规范化、便利化。', 'qmsxzfqzqd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (153, 58, '1）落实《法治中国建设规划（2020—2025年）》和《关于在公民中开展法治宣传教育的第八个五年规划(2021—2025年)》，加强法治宣传教育，开展群众性法治文化活动，全民法治宣传教育的普及率≥90%；', 'lsfzzgj', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (154, 58, '2）完善国家工作人员学法用法制度，把宪法法律列入党委中心组学习内容，列为党校（行政学院）必修课；', 'wsgjgzry', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (155, 58, '3）加强国家安全宣传教育、禁毒宣传教育、反恐怖宣传教育、反邪教宣传教育和反宗教极端思想宣传教育。', 'jqgjaqxc', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (156, 59, '1）加强企业、农村、机关、学校、科研院所、街道社区、社会组织和其他基层单位党的基层组织建设和群团组织建设，有效发挥他们的作用；', 'jqqync', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (157, 59, '2）严厉打击农村黑恶势力，坚决防止“村霸”等黑恶势力对基层政权的干扰侵蚀，加大对非法宗教活动和境外渗透活动的打击力度，依法制止利用宗教干预公共事务。', 'yldjnche', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (158, 60, '建立党建引领下的居民委员会、业主委员会、物业公司服务企业协调运行机制，充分调动居民参与积极性，形成党组织领导社区事务的民主协商、民主决策、民主管理和民主监督制度。', 'jldjylxdjm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (159, 61, '1）健全公共法律服务体系，加强公共法律服务实体平台、热线平台、网络平台建设，做好法律咨询、经济困难公民和特殊案件当事人法律援助服务、司法救助等工作；', 'jqggflfw', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (160, 61, '2）落实就业优先政策，健全就业公共服务体系、劳动关系协调机制、终身职业技能培训制度，保障劳动者待遇和权益；', 'lsjyyxzc', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (161, 61, '3）健全保护消费者合法权益的部门协作机制、社会共治机制，设立并畅通消费者投诉举报的渠道。', 'jqbhxfzh', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (162, 62, '1）加强婚姻家庭辅导服务和妇女权益保障，做好孤儿、残疾儿童、查找不到生父母的未成年人救助和收养安置工作，完善帮扶孤儿、事实无人抚养儿童相关制度，建立孤儿、事实无人抚养儿童基本生活最低养育标准自然增长机制，残疾儿童义务教育有保障；', 'jqhyjtfd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (163, 62, '2）扩大公益性岗位安置，帮扶残疾人、零就业家庭成员就业，健全残疾人关爱服务体系和帮扶制度，残疾人基本康复服务覆盖率≥85%；', 'kdgyxgwa', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (164, 62, '3）开展人口老龄化国情教育，加强养老服务设施建设，构建养老服务体系，健全养老服务综合监管制度，加快推进信息无障碍建设，加强老年人防范诈骗知识宣传。', 'kzrkllhg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (165, 63, '1）建立覆盖全社会的征信系统，建立健全以信用为基础的新型监管机制，构建适应高质量发展要求的社会信用体系；', 'jlfgqshd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (166, 63, '2）依法依规实施信用联合奖惩，健全信用修复制度，加快信用信息共享步伐。', 'yfygssxy', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (167, 64, '市文明委统筹协调文明委相关成员单位，针对群众反映强烈的诚信缺失突出问题开展集中治理，明确责任单位，列出任务清单，定期督促检查，群众对本市诚信建设的满意度≥90%。', 'swmwtcxdwm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (168, 65, '义务教育公民办结构合理，均衡配置公共教育资源，有具体的扶持弱校措施，实行免试就近入学和信息公开制度，推动义务教育优质均衡发展和城乡一体化，随迁子女、留守儿童、家庭经济困难学生受教育权利得到有效保障。', 'ywjygmbjgh', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (169, 66, '推行校务公开和收费公示制度，建立学校乱收费责任追究制度，政府部门定期开展教育收费专项检查，规范校外培训机构，学生参加线上线下校外培训频次、费用切实减少。', 'txxwgkhsfg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (170, 67, '加强科技馆、青少年科技活动中心等科普设施和科普基地建设，利用全国科技活动周、全国科普日、119全国消防日、122全国交通安全日、全国爱眼日、中国水周、世界水日、世界急救日、世界献血者日等时间节点，普及科学知识，开展群众性科普活动，弘扬科学精神，提高科学素质，公民具备科学素质的比例超过本省（区）平均水平（以最新中国公民科学素质抽样调查结果为准）。', 'jqkjgqsnk', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (171, 68, '1）根据国家基本公共文化服务指导标准，制定与本市经济社会发展水平相适应的地方标准，推进城乡公共文化服务体系一体建设，实施文化惠民工程，推动公共文化数字化建设；', 'gjgjjbgg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (172, 68, '2）市辖区域内有达到部颁二级以上标准的图书馆、文化馆，辖区内县（市、区）均建成图书馆总分馆制、文化馆总分馆制；', 'sxqynydd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (173, 68, '3）健全公共文化服务财政保障机制，确保财政公共文化投入与地方经济社会发展水平相适应。', 'jqggwhfw', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (174, 69, '1）完善公共文化设施免费开放的保障机制，公共图书馆、公共博物馆(非文物建筑及遗址类)、公共美术馆、文化馆、纪念馆、科技馆等免费开放、基本服务项目健全；', 'wsggwhss', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (175, 69, '2）开展全民阅读活动，建设书香社会，支持实体书店发展，科学规划和建设城市书报刊亭，推动数字化文化传播与服务；', 'kzqmydhd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (176, 69, '3）开展全民健身活动，经常参加体育锻炼人数占比≥37.2%，中小学生每天校内体育活动时间≥1小时，《国民体质测定标准》合格以上的人数比例≥90%。', 'kzqmjshd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (177, 70, '1）街道（乡镇）综合文化站、社区（行政村）综合文化服务中心与新时代文明实践所、站协同推进，经常性开展宣传文化、党员教育、市民教育、科学普及、普法教育等活动；', 'jdxzzh', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (178, 70, '2）城乡社区均建有全民健身场地设施且设施状况良好，新建居住区和社区人均室外体育用地面积≥0.3平方米、人均室内体育用地建筑面积≥0.1平方米，人均体育场地面积≥2.3平方米。', 'cxsqjjyq', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (179, 71, '落实国家文化产业规划和政策，完善文化产业发展政策环境，加强文化市场体系建设，扩大优质文化产品供给，发展新型文化企业、文化业态、文化消费模式，提高文化消费便利性。', 'lsgjwhcygh', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (180, 72, '坚持各民族一律平等，全面贯彻党的民族政策，深化民族团结进步宣传教育，促进各民族交往交流交融，推进民族事务治理体系和治理能力现代化，铸牢中华民族共同体意识，实现各民族共同团结奋斗、共同繁荣发展。', 'jcgmzylpd', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (181, 73, '完善科技创新体制机制，全社会研发支出占GDP比重、科技进步贡献率、全员劳动生产率逐年提升。', 'wskjcxtzjz', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (182, 74, '健全工资合理增长机制，提高低收入群体收入，扩大中等收入群体，城乡居民人均可支配收入水平高于全国或本省（区）同类城市平均水平，或城乡居民人均可支配收入年增长率高于全国或本省（区）同类城市平均增长率。', 'jqgzhlzzjz', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (183, 75, '1）突出以人为本、尊重自然、传承历史、绿色低碳的理念，科学制定国土空间规划，加强对城市空间立体性、平面协调性、风貌整体性、文脉延续性、环境友好性、生活宜居性的规划和管控，实施城市更新行动，推进城市生态修复、功能完善工程，全面提升城市品质；', 'tcyrwbz', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (184, 75, '2）加强智慧城市建设，推动城市信息模型（CIM）基础平台、城市运行管理服务平台建设，构建城市数据资源体系，推进城市运行“一网统管”。', 'jqzhcsjs', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (185, 76, '3）建成区公共卫生间设置密度≥4座/平方公里，生活垃圾处理能力与清运量的比例≥1.2，生活垃圾分类覆盖率≥70%且逐年提高，道路清扫覆盖率≥90%，道路机械化清扫率≥80%，渣土全流程监管达标率≥90%。', 'jcqggwsj', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (186, 77, '3）建成区公园绿地服务半径覆盖率≥80%，建成区绿地率≥36%或逐年提升，人均公园绿地面积≥12平方米或逐年提升，道路照明亮灯率≥95%。', 'jcqgyldf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (187, 78, '1）社会用语用字文明规范，地名管理使用规范有序，机动车、非机动车有序规范停放、不占用绿化带、不影响行人通行，共享单车文明使用、规范停放，城市公共场所无占道经营、乱搭乱建现象，无流浪乞讨人员滋扰他人、扰乱社会秩序现象；', 'shyyyzwm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (188, 78, '3）优化街区路网结构和级配关系，建成区内道路长度与建成区面积的比值≥8公里/平方公里，建成区道路面积率≥15%，建成区公交站点覆盖率≥90%，停车位与小汽车保有量的比例≥1.1。', 'yhjqlwjg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (189, 79, '1）加强城乡社区治理和服务体系建设，健全城乡社区综合服务设施网络，构建网格化管理、精细化服务、信息化支撑、开放共享的基层管理服务平台，建设包括便民市场、运动场地、文化活动中心、社区服务中心、医疗服务机构在内的15分钟生活圈，加快推进老旧小区改造，完善小区配套基础设施、公共服务设施；', 'jqcxsqzl', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (190, 80, '1）落实《健康中国2030规划纲要》，根据《关于开展健康城市健康村镇建设的指导意见》，制定本地区具体区域卫生规划和医疗机构设置规划，千人口医疗卫生机构床位数、每千常住人口公共卫生人员数符合所在地区域卫生规划要求，市辖区建成至少1个符合运行要求的医疗废物集中处置设施，个人卫生支出占卫生总费用的比例≤28%，辖区人均预期寿命≥77.7岁；', 'lsjkzg', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (191, 80, '2）完善突发公共卫生事件监测预警处置机制，健全医疗救治、科技支撑、物资保障体系，健全社会心理服务体系和危机干预机制，提高应对突发公共卫生事件能力；', 'wstfggws', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (192, 80, '3）深入推进爱国卫生运动，推进医养结合发展，依托本地医疗卫生服务机构和宣传文化阵地普及卫生健康知识、传染病防控和应急救护知识，全市居民健康素养水平逐年提升不低于1个百分点。', 'srtjagws', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (193, 81, '健全基本养老、基本医疗保险筹资和待遇调整机制，发展多层次、多支柱养老保险体系，完善重大疾病医疗保险和救助制度，健全分层分类的社会救助体系。', 'jqjbyljby', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (194, 82, '开展军民共建精神文明活动，强化全民国防教育和双拥宣传，健全退役军人工作体系和保障制度，落实优抚政策，做好军队转业干部和退役士兵安置、随军家属就业、军人子女入学等工作，退役军人失业率低于城市调查失业率。', 'kzjmgjjswm', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (195, 83, '1）加强社会治安防控体系建设，加强消防安全防控体系建设，完善公共安全视频监控建设联网应用，社会面、重点单位及社区物防、技防、人防水平符合安全要求；', 'jqshzafk', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (196, 83, '2）加强城市生命线系统建设，推进海绵城市、韧性城市建设，增强城市防洪排涝能力，管线、给排水、电力等设施完好，建（构）筑物及依附于建（构）筑物的玻璃幕墙、展板等安全牢固、完好无损，户外广告设施和招牌设置牢固可靠，用户龙头水合格率达到100%，生活污水集中收集率≥75%，建成区人均避难场所面积≥1.5平方米，窨井盖完好率≥98%，年度“生命线”事故数逐年下降。', 'jqcssmxx', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (197, 84, '1）农贸（集贸、批发）市场不出售过期、变质、伪劣食品，实行生熟分开、干湿分离，独立设置活禽售卖宰杀区域或无活禽宰杀现象，无违规售卖野生动物现象，农产品质量安全监测合格率≥98%；', 'nmjmpf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (198, 84, '2）提高食品药品安全保障水平，食品药品安全事件及时查处，无漏报、瞒报情况。', 'tgspypaq', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (199, 85, '1）建立健全减灾、防灾、抗灾、救灾综合协调机制和灾害应急管理体系，开展应急通信专用网络和应急指挥系统建设，建立覆盖城乡的应急救援体系力量，加强应急物资保障体系建设；', 'jljqjzf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (200, 85, '2）开展社区减灾、防灾及应急宣传教育，开展自救互救及应急救护知识与技能培训。', 'kzsqjzf', NULL, NULL, '0', 50, 1, 3);
INSERT INTO `evaluation_rules` VALUES (201, 86, '完善和落实安全生产责任制，加强安全生产监管执法，亿元国内生产总值生产安全事故死亡率低于全国或本省（区）同类城市平均水平。', 'wshlsaqscz', NULL, NULL, '0', 50, 1, 3);
