INSERT INTO `evaluation_rules` VALUES (203, 87, '2）卖淫嫖娼、聚众赌博、开设赌场、吸毒贩毒制毒非法种植毒品原植物等违法犯罪得到有效控制，做好对刑满释放人员、吸毒人员、涉诈重点人员、肇事肇祸严重精神障碍患者等重点人群的服务管理，没有下辖县（市、区）被列入国家禁毒委员会重点整治地区名单。', 'mypcjzd', NULL, NULL, '0', 50, 1, 3);
