INSERT INTO `evaluation_rules` VALUES (207, 90, '2）城市市辖区水质优良（达到或优于Ⅲ类）比例连续三年上升或达到85%，城市地表水监测断面水质达标率连续三年达到90%以上，城市地下水未超标或超采区地下水水位连续三年上升；', 'cssxqszy', NULL, NULL, '0', 50, 1, 3);
